/**
 * 14 / 14 test cases passed.
 * Status: Accepted
 * Runtime: 384 ms
 */
class Solution {
    fun isSubsequence(s: String, t: String): Boolean {
        if (s.isEmpty()) return true
        if (t.isEmpty()) return false
        val i = t.indexOf(s[0])
        if (i == -1) return false
        if (s.length == 1 && i != -1) return true
        return isSubsequence(s.substring(1), t.substring(i + 1))
    }
}

/**
 * 14 / 14 test cases passed.
 * Status: Accepted
 * Runtime: 292 ms
 */
class Solution2 {
    fun isSubsequence(s: String, t: String): Boolean {
        var pos = -1
        return s.none { t.indexOf(it, pos + 1).also { pos = it } == -1 }
    }
}

fun main(args: Array<String>) {
    Solution2().isSubsequence("abc", "ahbgdc").let(::println)
    Solution2().isSubsequence("axc", "ahbgdc").let(::println)
    Solution2().isSubsequence("abc", "cba").let(::println)
    Solution2().isSubsequence("", "cba").let(::println)
    Solution2().isSubsequence("abc", "").let(::println)
    Solution2().isSubsequence("leeeeetcode", "").let(::println)
}