class Solution {
    fun addStrings(num1: String, num2: String): String {
        val result = StringBuilder()
        val a = if (num1.length >= num2.length) num1.reversed() else num2.reversed()
        val b = if (num2.length <= num1.length) num2.reversed() else num1.reversed()
        var carry = 0

        (0 until a.length).takeWhile { it <= b.length - 1 }.forEach {
            val add = a[it].toInt() - '0'.toInt() + b[it].toInt() - '0'.toInt() + carry
            carry = add / 10
            result.append(add % 10)
        }

        (b.length until a.length).forEach {
            val add = a[it].toInt() - '0'.toInt() + carry
            carry = add / 10
            result.append(add % 10)
        }

        if (carry == 1) result.append(carry)
        return result.reversed().toString()
    }
}

